  private Runnable createFinish;

  public void finish() {
    if (createFinish != null) {
      createFinish.run();
      createFinish = null;
      subPanel.removeAll();
    }
  }

  public void createCube() {
    finish();
    CreateCubePanel panel = new CreateCubePanel(this);
    createFinish = (Runnable)panel;
    subPanel.add(panel);
  }
